println("Starting analysis of puzzle input")

left_count = 0
right_count = 0
current_floor = 0
basement_flag = false

for (index,c) in enumerate(puzzle_input)
    if c == '('
        left_count += 1
        current_floor += 1
    elseif c == ')'
        right_count += 1
        current_floor -= 1
    end
    if current_floor == -1 && basement_flag == false
        basement_flag = true
        println("The position at which Santa first enters the basement is $index")
    end

end

end_floor = left_count - right_count
println("The floor that Santa should visit is $end_floor")